mapper.select:
select a.id,a.username,a.real_name realName,a.user_image userImage,a.create_time createTime,a.is_status isStatus ,CAST(group_concat(r.name) as char) as roles from u_admin_user a INNER JOIN u_user_role u ON a.id = u.user_id INNER JOIN u_role r ON u.role_id = r.id where a.is_status = 1
and a.username like CONCAT ('%',#{username},'%')
and a.is_status = #{isStatus}
and u.id = #{roleId}
GROUP BY a.id,a.username,a.real_name,a.user_image,a.create_time,a.is_status ORDER BY a.create_time DESC LIMIT #{page},#{size}

select count(1) count from u_admin_user a INNER JOIN u_user_role u ON a.id = u.user_id INNER JOIN u_role r ON u.role_id = r.id where a.is_status = 1
and a.username like CONCAT ('%',#{username},'%')
and a.is_status = #{isStatus}
and u.id = #{roleId}
GROUP BY a.id,a.username,a.real_name,a.user_image,a.create_time,a.is_status
